const topBarMarkup = (
  <TopBar
    showNavigationToggle
    userMenu={userMenuMarkup}
    //    onNavigationToggle={handleNavigationToggle}
  />
);

export default function App() {
  return <Frame topBar={topBarMarkup} />;
}
